function setIndicatorLight() {
  // Retrieve the binary variables from text elements
  var firstVar = parseInt(document.getElementById('firstVar').textContent.replace(/\$/g, ''));
  var secondVar = parseInt(document.getElementById('secondVar').textContent.replace(/\$/g, ''));

  var indicator = document.getElementById('indicator');

  if (secondVar === 1) {
    indicator.setAttribute('class', 'red');
  } else if (firstVar === 1) {
    indicator.setAttribute('class', 'yellow');
  } else {
    indicator.setAttribute('class', 'green');
  }
}

// Call the function to set the indicator light
setIndicatorLight();
