// Function to set the indicator light based on binary variables
function setIndicatorLight(firstVar, secondVar) {
  var indicator = document.getElementById('indicator');
  if (secondVar === 1) {
    indicator.setAttribute('class', 'red');
  } else if (firstVar === 1) {
    indicator.setAttribute('class', 'yellow');
  } else {
    indicator.setAttribute('class', 'green');
  }
}

// Example usage
var firstBinaryVar = 1; // Set to 0 or 1
var secondBinaryVar = $n2$; // Set to 0 or 1

setIndicatorLight(firstBinaryVar, secondBinaryVar);
